mapper.insert:
insert into item (
	item_name, price, description, picture_url, preview_url

)values (
	#{itemName}, #{price}, #{description}, #{pictureUrl}, #{previewUrl}
	)

mapper.update:
update item
set item_name = #{itemName}
	,price = #{price}
	,description = #{description}
	,picture_url = #{pictureUrl}
	,preview_url = #{previewUrl}
where item_id = #{itemId}

mapper.select:
select picture_url
from item
where item_id = #{itemId}

select preview_url
from item
where item_id = #{itemId}
